// components/Inventory/AddProduct.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddSupplierModal from '../Suppliers/AddSupplierModal';
import Select from 'react-select';
import '../../Styles/AddProduct.css';

export default function AddProduct({ onClose }) {
  const [formData, setFormData] = useState({
    name: '',
    model_no: '',
    price: '',
    size: '',
    pieces: '',
  });

  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState('');
  const [suppliers, setSuppliers] = useState([]);
  const [selectedSupplier, setSelectedSupplier] = useState(null);
  const [showSupplierModal, setShowSupplierModal] = useState(false);

  useEffect(() => {
    const fetchSuppliers = async () => {
      try {
        const res = await axios.get('http://localhost:8000/api/suppliers/');
        setSuppliers(res.data);
      } catch (err) {
        console.error('Failed to load suppliers:', err);
      }
    };
    fetchSuppliers();
  }, []);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value,
    }));
  };

  const addManualProduct = () => {
    const { name, model_no, price, size, pieces } = formData;

    if (!name || !model_no || !price || !size || !pieces) {
      setMessage('❌ Please fill all fields');
      return;
    }

    if (!selectedSupplier) {
      setMessage('❌ Please select a supplier for the product');
      return;
    }

    setProducts(prev => [
      ...prev,
      {
        ...formData,
        supplier: selectedSupplier.value,
      },
    ]);
    setFormData({
      name: '',
      model_no: '',
      price: '',
      size: '',
      pieces: '',
    });
    setSelectedSupplier(null);
    setMessage('');
  };

  const handleSubmit = async () => {
    try {
      const payload = new FormData();

      products.forEach((product, index) => {
        payload.append(`products[${index}][name]`, product.name);
        payload.append(`products[${index}][model_no]`, product.model_no);
        payload.append(`products[${index}][price]`, product.price);
        payload.append(`products[${index}][size]`, product.size);
        payload.append(`products[${index}][pieces]`, product.pieces);
        if (product.supplier) {
          payload.append(`products[${index}][supplier]`, product.supplier);
        }
      });

      const res = await axios.post('http://localhost:8000/api/products/', payload);
      setMessage('✅ Products added successfully');
      setProducts([]);
    } catch (err) {
      console.error(err);
      setMessage('❌ Failed to add products');
    }
  };

  return (
    <div className="add-product-container">
      <h2>Add Product</h2>

      <div className="supplier-section">
        <label>Select Supplier*</label>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <Select
            options={suppliers.map(s => ({ value: s.id, label: s.name }))}
            value={selectedSupplier}
            onChange={setSelectedSupplier}
            placeholder="Choose a supplier"
            isClearable
            className="supplier-select"
          />
          <button
            type="button"
            onClick={() => setShowSupplierModal(true)}
            className="add-button small"
          >
            + Add New
          </button>
        </div>
      </div>

      <div className="form-group">
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={formData.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="model_no"
          placeholder="Model No"
          value={formData.model_no}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="size"
          placeholder="Size"
          value={formData.size}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="pieces"
          placeholder="Pieces"
          value={formData.pieces}
          onChange={handleInputChange}
        />
      </div>

      <button onClick={addManualProduct}>Add Product to List</button>

      <div className="product-preview">
        {products.map((product, i) => (
          <div key={i}>
            {product.name} - {product.model_no} - {product.price} - Supplier ID: {product.supplier}
          </div>
        ))}
      </div>

      {message && <div className="message">{message}</div>}

      <div className="button-group">
        <button onClick={handleSubmit}>Submit All</button>
        <button onClick={onClose}>Close</button>
      </div>

      {showSupplierModal && (
        <AddSupplierModal
          onClose={() => {
            setShowSupplierModal(false);
            axios.get('http://localhost:8000/api/suppliers/').then(res => setSuppliers(res.data));
          }}
        />
      )}
    </div>
  );
}
