import React, { useState } from "react";
import "../../Styles/invoice.css";

export default function Invoice() {
  const [showModal, setShowModal] = useState(false);
  const [file, setFile] = useState(null);
  const [tableData, setTableData] = useState([]);
  const [billNumber, setBillNumber] = useState("");
  const [invoiceList, setInvoiceList] = useState([]); // ✅ List of all submitted invoices

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);

    // Simulated data parsing
    const dummyData = [
      { id: 1, name: "Dominos", model: "A2485", price: "189999", size: "16 inch", quantity: 5 },
      { id: 2, name: "Pepsi", model: "P1234", price: "599", size: "1L", quantity: 10 },
    ];
    setTableData(dummyData);
    setBillNumber("INV" + Date.now()); // Unique bill number
  };

  const handleDelete = (id) => {
    setTableData(prev => prev.filter(item => item.id !== id));
  };

  const handleInputChange = (id, field, value) => {
    setTableData(prev =>
      prev.map(item => item.id === id ? { ...item, [field]: value } : item)
    );
  };

  const handleSubmit = () => {
    const newInvoice = {
      billNumber,
      products: tableData,
    };

    // ✅ Save invoice to local state
    setInvoiceList(prev => [...prev, newInvoice]);

    // Reset modal
    setShowModal(false);
    setTableData([]);
    setFile(null);
    setBillNumber("");
  };

  return (
    <div className="invoice-container">
      <h2>Invoice List</h2>
      <button className="add-btn" onClick={() => setShowModal(true)}>+ Add Invoice</button>

      <ul className="bill-list">
        {invoiceList.map((invoice, index) => (
          <li key={index} onClick={() => alert(JSON.stringify(invoice, null, 2))}>
            {invoice.billNumber}
          </li>
        ))}
      </ul>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Add New Invoice</h3>
            <input type="file" accept=".pdf,.png,.jpg" onChange={handleFileChange} />

            {tableData.length > 0 && (
              <>
                <table>
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Model ID</th>
                      <th>Price</th>
                      <th>Size</th>
                      <th>Quantity</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {tableData.map(item => (
                      <tr key={item.id}>
                        <td><input value={item.name} onChange={(e) => handleInputChange(item.id, "name", e.target.value)} /></td>
                        <td><input value={item.model} onChange={(e) => handleInputChange(item.id, "model", e.target.value)} /></td>
                        <td><input value={item.price} onChange={(e) => handleInputChange(item.id, "price", e.target.value)} /></td>
                        <td><input value={item.size} onChange={(e) => handleInputChange(item.id, "size", e.target.value)} /></td>
                        <td><input value={item.quantity} onChange={(e) => handleInputChange(item.id, "quantity", e.target.value)} /></td>
                        <td><button className="delete-btn" onClick={() => handleDelete(item.id)}>Delete</button></td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <button className="submit-btn" onClick={handleSubmit}>Submit Invoice</button>
              </>
            )}

            <button className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}
