import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../../Styles/Login.css'; // Reuse the same CSS
import axios from 'axios';
import PopupDialog from './PopupDialog';

function Signup() {
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [popup, setPopup] = useState({ open: false, type: 'success', title: '', message: '' });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('http://localhost:8000/api/accounts/register/', formData);
      setPopup({ open: true, type: 'success', title: 'Signup Successful', message: 'Your account has been created successfully.' });
      setTimeout(() => {
        setPopup({ ...popup, open: false });
        navigate('/login');
      }, 1200);
    } catch (err) {
      setPopup({ open: true, type: 'error', title: 'Signup Failed', message: 'Signup failed: ' + (err.response?.data?.username || err.response?.data?.email || 'Please check your details and try again.') });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="main-background">
      <PopupDialog {...popup} onClose={() => setPopup({ ...popup, open: false })} />
      <div className="main-box">
        <div className="form-box">
          <h2>Create Your Account</h2>
          <button className="google-btn">
            <svg className="google-icon" viewBox="0 0 24 24" width="24" height="24">
              <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
              <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
              <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
              <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
            </svg>
            <span>Sign up with Google</span>
          </button>
          <div className="divider"><span>OR SIGN UP WITH EMAIL</span></div>
          <form onSubmit={handleSubmit}>
            <input name="username" placeholder="Username" onChange={handleChange} required />
            <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
            <div style={{ position: 'relative' }}>
              <input
                name="password"
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                onChange={handleChange}
                required
                style={{ paddingRight: '40px' }}
              />
              <span
                onClick={() => setShowPassword((v) => !v)}
                style={{
                  position: 'absolute',
                  right: '12px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  cursor: 'pointer',
                  color: '#aaa',
                  fontSize: '20px',
                  userSelect: 'none',
                }}
                tabIndex={0}
                aria-label={showPassword ? 'Hide password' : 'Show password'}
              >
                {showPassword ? (
                  <svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="#2d2a81" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M17.94 17.94A10.06 10.06 0 0 1 12 20C7 20 2.73 16.11 1 12c.73-1.67 2.1-3.87 4.06-5.94M9.88 9.88A3 3 0 0 1 12 9c1.66 0 3 1.34 3 3 0 .39-.08.76-.22 1.1" /><path d="M1 1l22 22" /></svg>
                ) : (
                  <svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="#2d2a81" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M1 12S5 4 12 4s11 8 11 8-4 8-11 8S1 12 1 12z" /><circle cx="12" cy="12" r="3" /></svg>
                )}
              </span>
            </div>
            <button type="submit" className="login-btn" disabled={loading}>{loading ? 'Signing up...' : 'Sign Up'}</button>
          </form>
          <p className="signup-link">Already have an account? <Link to="/login">Log in</Link></p>
        </div>
        <div className="image-box">
          <img src="/ima.png" alt="Profile" width="400" />
        </div>
      </div>
    </div>
  );
}

export default Signup;
