import React, { useState } from 'react';
import axios from 'axios';
import '../../Styles/AddSupplierModal.css';

export default function AddSupplierModal({ onClose, refreshSuppliers }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:8000/api/suppliers/', formData);
      console.log('Supplier added:', response.data);
      refreshSuppliers(); // Refresh list or close modal
      onClose();
    } catch (err) {
      if (err.response) {
        // Backend returned a response with error code (e.g., 400)
        const errorData = err.response.data;

        if (errorData.error) {
          setError(errorData.error);  // Custom error from Django
        } else if (typeof errorData === 'object') {
          // Validation errors
          const firstKey = Object.keys(errorData)[0];
          setError(errorData[firstKey][0]);
        } else {
          setError('An error occurred. Please try again.');
        }

      } else if (err.request) {
        // No response received from server
        setError('Server not responding. Please try again later.');
      } else {
        // Other errors (e.g., coding issues)
        setError('An error occurred. Please check your form.');
      }
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Add New Supplier</h2>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>

        <form className="modal-form" onSubmit={handleSubmit}>
          <label>Supplier Name*</label>
          <input type="text" name="name" required onChange={handleChange} />

          <label>Email*</label>
          <input type="email" name="email" required onChange={handleChange} />

          <label>Phone</label>
          <input type="tel" name="phone" onChange={handleChange} />

          <label>Address</label>
          <textarea rows={2} name="address" onChange={handleChange} />

          {error && (
            <div className="error-message" style={{ color: 'red', marginTop: '10px', textAlign: 'center' }}>
              {error}
            </div>
          )}

          <div className="modal-buttons">
            <button type="button" className="cancel-btn" onClick={onClose}>Cancel</button>
            <button type="submit" className="save-btn">Save Supplier</button>
          </div>
        </form>
      </div>
    </div>
  );
}
