import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Supplier.css'; // Make sure this exists

export default function Supplier() {
  const [suppliers, setSuppliers] = useState([]);
  const [selectedSupplier, setSelectedSupplier] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchSuppliers();
  }, []);

  const fetchSuppliers = async () => {
    try {
      const res = await axios.get('/api/suppliers/');
      setSuppliers(res.data);
    } catch (err) {
      console.error('Error fetching suppliers:', err);
    }
  };

  const deleteSupplier = async (id) => {
    if (window.confirm('Are you sure you want to delete this supplier?')) {
      try {
        await axios.delete(`/api/suppliers/${id}/`);
        fetchSuppliers();
        if (selectedSupplier?.id === id) setSelectedSupplier(null); // clear selection
      } catch (err) {
        console.error('Error deleting supplier:', err);
      }
    }
  };

  const fetchSupplierProducts = async (supplierId) => {
    try {
      const res = await axios.get(`/api/suppliers/${supplierId}/products/`);
      setProducts(res.data);
    } catch (err) {
      console.error('Error fetching supplier products:', err);
    }
  };

  const handleCardClick = (supplier) => {
    setSelectedSupplier(supplier);
    fetchSupplierProducts(supplier.id);
  };

  return (
    <div className="supplier-page">
      <h2>Supplier Directory</h2>
      <div className="supplier-grid">
        {suppliers.map((supplier) => (
          <div key={supplier.id} className="supplier-card" onClick={() => handleCardClick(supplier)}>
            <img src={supplier.image} alt={supplier.name} className="supplier-img" />
            <div className="supplier-info">
              <h3>{supplier.name}</h3>
              <p>Type: {supplier.supplier_type}</p>
              <p>Category: {supplier.category}</p>
              <div className="card-actions">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteSupplier(supplier.id);
                  }}
                  className="delete-btn"
                >
                  Delete
                </button>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    // handleEdit(supplier.id); // Optional
                  }}
                  className="edit-btn"
                >
                  Edit
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {selectedSupplier && (
        <div className="supplier-details">
          <h2>Supplier Details</h2>
          <p><strong>Name:</strong> {selectedSupplier.name}</p>
          <p><strong>Type:</strong> {selectedSupplier.supplier_type}</p>
          <p><strong>Category:</strong> {selectedSupplier.category}</p>
          <h3>Supplied Products:</h3>
          {products.length > 0 ? (
            <ul>
              {products.map((p) => (
                <li key={p.id}>{p.name}</li>
              ))}
            </ul>
          ) : (
            <p>No products found for this supplier.</p>
          )}
        </div>
      )}
    </div>
  );
}
