import React, { useState } from 'react';
import axios from 'axios';

const AddProduct = () => {
  const [formData, setFormData] = useState({
    name: '',
    model_no: '',
    price: '',
    size: '',
    pieces: '',
    image: null,
  });

  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState('');


  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData({ ...formData, image: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handlePdfUpload = async (e) => {
    const file = e.target.files[0];
    const data = new FormData();
    data.append('pdf', file);
    const res = await axios.post('http://localhost:8000/api/extract-pdf/', data);
    setProducts([...products, ...res.data.products]);
  };

  const addManualProduct = () => {
    setProducts([...products, formData]);
    setFormData({
      name: '',
      model_no: '',
      price: '',
      size: '',
      pieces: '',
      image: null,
    });
  };

  const handleDeleteProduct = (indexToRemove) => {
    setProducts(products.filter((_, index) => index !== indexToRemove));
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  const payload = new FormData();

  products.forEach((product, index) => {
    payload.append(`products[${index}][name]`, product.name);
    payload.append(`products[${index}][model_no]`, product.model_no);
    payload.append(`products[${index}][price]`, product.price);
    payload.append(`products[${index}][size]`, product.size);
    payload.append(`products[${index}][pieces]`, product.pieces);
    if (product.image) {
      payload.append(`products[${index}][image]`, product.image);
    }
  });

  await axios.post('http://localhost:8000/api/add-product/', payload, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });

  setMessage('✅ Products added successfully!');
  setTimeout(() => {
    window.location.href = '/inventory';
  }, 1500);
};

  return (
    <div style={{ padding: '30px', maxWidth: '800px', margin: 'auto' }}>
      <h2 style={{ marginBottom: '20px' }}>Add New Product</h2>

      {/* Form Inputs */}
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', marginBottom: '20px' }}>
        <input name="name" value={formData.name} onChange={handleChange} placeholder="Product Name" required style={inputStyle} />
        <input name="model_no" value={formData.model_no} onChange={handleChange} placeholder="Model ID" required style={inputStyle} />
        <input name="price" value={formData.price} onChange={handleChange} placeholder="Price" required style={inputStyle} />
        <input name="size" value={formData.size} onChange={handleChange} placeholder="Size" required style={inputStyle} />
        <input name="pieces" value={formData.pieces} onChange={handleChange} placeholder="Quantity" required style={inputStyle} />

        <label style={fileLabelStyle}>
          Select Bill
          <input type="file" accept="application/pdf" onChange={handlePdfUpload} style={{ display: 'none' }} />
        </label>

        <button type="button" onClick={addManualProduct} style={buttonStyle}>Add Product</button>
      </div>

      {/* Product Table */}
      {products.length > 0 && (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginBottom: '20px' }}>
          <thead>
            <tr style={{ backgroundColor: '#f0f0f0' }}>
              <th style={cellStyle}>Product Name</th>
              <th style={cellStyle}>Model ID</th>
              <th style={cellStyle}>Price</th>
              <th style={cellStyle}>Size</th>
              <th style={cellStyle}>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p, index) => (
              <tr key={index}>
                <td style={cellStyle}>{p.name}</td>
                <td style={cellStyle}>{p.model_no}</td>
                <td style={cellStyle}>{p.price}</td>
                <td style={cellStyle}>{p.size}</td>
                <td style={{ ...cellStyle, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  {p.pieces}
                  <button
                    onClick={() => handleDeleteProduct(index)}
                    style={deleteButtonStyle}
                  >
                    −
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Save / Cancel */}
      {products.length > 0 && (
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
          <button type="button" onClick={handleSubmit} style={saveButtonStyle}>Save</button>
          <button type="button" onClick={() => window.location.href = '/inventory'} style={cancelButtonStyle}>Cancel</button>
          {message && (
          <div style={{ marginBottom: '10px', color: 'green', fontWeight: 'bold' }}>
          {message}
          </div>
)}

        </div>
      )}
    </div>
  );
};

// Styles
const inputStyle = {
  padding: '10px',
  flex: '1 1 45%',
  minWidth: '180px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const buttonStyle = {
  padding: '10px 16px',
  backgroundColor: '#007bff',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

const cellStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  textAlign: 'left',
};

const fileLabelStyle = {
  padding: '10px 16px',
  backgroundColor: '#f0f0f0',
  border: '1px solid #ccc',
  borderRadius: '4px',
  cursor: 'pointer',
  display: 'inline-block',
  minWidth: '140px',
  textAlign: 'center',
};

const deleteButtonStyle = {
  backgroundColor: '#e0e0e0',
  color: 'white',
  border: 'none',
  borderRadius: '50%',
  width: '22px',
  height: '22px',
  cursor: 'pointer',
  fontWeight: 'bold',
  marginLeft: '10px',
};

const saveButtonStyle = {
  backgroundColor: '#1a73e8',
  color: 'white',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '4px',
  cursor: 'pointer',
};

const cancelButtonStyle = {
  backgroundColor: '#e0e0e0',
  color: '#333',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '4px',
  cursor: 'pointer',
};

export default AddProduct;
