// components/AddSupplierModal.js
import React, { useState } from 'react';
import '../../Styles/AddSupplierModal.css';

export default function AddSupplierModal({ onClose, onSupplierAdded }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [businessType, setBusinessType] = useState('Manufacturer');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newSupplier = {
      name,
      email,
      phone,
      address,
      business_type: businessType,
    };

    try {
      const response = await fetch('http://localhost:8000/api/suppliers/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newSupplier),
      });

      if (response.ok) {
        const createdSupplier = await response.json();
        onSupplierAdded(createdSupplier);  // Update parent list
        onClose();
      } else {
        alert('Error adding supplier');
      }
    } catch (error) {
      console.error('Failed to add supplier:', error);
      alert('Error connecting to server');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Add New Supplier</h2>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>

        <form className="modal-form" onSubmit={handleSubmit}>
          <label>Supplier Name*</label>
          <input type="text" required value={name} onChange={(e) => setName(e.target.value)} />

          <label>Email*</label>
          <input type="email" required value={email} onChange={(e) => setEmail(e.target.value)} />

          <label>Phone</label>
          <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />

          <label>Address</label>
          <textarea rows={2} value={address} onChange={(e) => setAddress(e.target.value)} />

          <label>Business Type</label>
          <select value={businessType} onChange={(e) => setBusinessType(e.target.value)}>
            <option>Manufacturer</option>
            <option>Distributor</option>
            <option>Wholesaler</option>
          </select>

          <div className="modal-buttons">
            <button type="button" className="cancel-btn" onClick={onClose}>Cancel</button>
            <button type="submit" className="save-btn">Save Supplier</button>
          </div>
        </form>
      </div>
    </div>
  );
}
