import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../Styles/SupplierDetail.css'

export default function SupplierDetail({ supplier }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchSupplierProducts();
  }, []);

  const fetchSupplierProducts = async () => {
    const response = await axios.get(`/api/suppliers/${supplier.id}/products/`);
    setProducts(response.data);
  };

  return (
    <div className="supplier-detail">
      <h2>{supplier.name}</h2>
      <p><strong>Type:</strong> {supplier.supplier_type}</p>
      <p><strong>Category:</strong> {supplier.category}</p>

      <h3>Supplied Products</h3>
      {products.length > 0 ? (
        <ul>
          {products.map((prod) => (
            <li key={prod.id}>{prod.name}</li>
          ))}
        </ul>
      ) : (
        <p>No products listed.</p>
      )}
    </div>
  );
}
