import React, { useState, useEffect } from "react";
import {
    Tooltip, ResponsiveContainer,
       PieChart, Pie, Cell,Legend
     } from "recharts";
     import "../../Styles/Dashboard.css"; 
   export default function Right(){
         const COLORS = ["#00FFFF", "#00BFFF", "#1E90FF", "#0000FF", "#FF6B6B"];
         const [pieData, setPieData] = useState([]);
         const [loading, setLoading] = useState(true);

         useEffect(() => {
             fetchSupplierPieChartData();
         }, []);

         const fetchSupplierPieChartData = async () => {
             try {
                 const response = await fetch('http://localhost:8000/api/supplier-pie-chart/');
                 if (response.ok) {
                     const data = await response.json();
                     setPieData(data);
                 } else {
                     console.error('Failed to fetch supplier pie chart data');
                 }
             } catch (error) {
                 console.error('Error fetching supplier pie chart data:', error);
             } finally {
                 setLoading(false);
             }
         };
       
         const typeData = [
           { name: "Jeans", value: 70 },
           { name: "T shirts", value: 28 },
           { name: "Salwars", value: 50 },
           { name: "Sarees", value: 65 },
           { name: "Skirts", value: 12 },
           { name: "Ethnic", value: 10 },
         ];
       
       
       return(
           <div className="right-container">
           <div className="chart-card">
               <h3>Top 3 Suppliers by Product Count</h3>
               {loading ? (
                   <div style={{ 
                       height: '250px', 
                       display: 'flex', 
                       alignItems: 'center', 
                       justifyContent: 'center',
                       color: '#dbeafe'
                   }}>
                       Loading supplier data...
                   </div>
               ) : pieData.length === 0 ? (
                   <div style={{ 
                       height: '250px', 
                       display: 'flex', 
                       alignItems: 'center', 
                       justifyContent: 'center',
                       color: '#dbeafe'
                   }}>
                       No supplier data available
                   </div>
               ) : (
                   <ResponsiveContainer width="100%" height={250}>
                       <PieChart>
                       <Pie
                           data={pieData}
                           dataKey="value"
                           cx="40%"
                           cy="50%"
                           innerRadius={50}
                           outerRadius={80}
                           paddingAngle={3}
                           stroke="none"
                       >
                           {pieData.map((entry, index) => (
                           <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                           ))}
                       </Pie>
                       <Tooltip 
                           formatter={(value, name) => [value, 'Products']}
                           labelStyle={{ color: '#1f2937' }}
                       />
                       <Legend
                           layout="vertical"
                           align="right"
                           verticalAlign="middle"
                           formatter={(value, entry, index) => (
                           <span style={{ color: COLORS[index] }}>{value}</span>
                           )}
                       />
                       </PieChart>
                   </ResponsiveContainer>
               )}
               </div>
           <div className="progress-card">
             <h3>Orders by Type</h3>
             {typeData.map((type, i) => (
               <div key={i} className="progress-item">
                 <div className="progress-label">
                   <span>{type.name}</span>
                   <span>{type.value}%</span>
                 </div>
                 <div className="progress-bar">
                   <div
                     className="progress-fill"
                     style={{
                       width: `${type.value}%`,
                       backgroundColor: i % 2 === 0 ? "#4f46e5" : "#06b6d4",
                     }}
                   ></div>
                 </div>
               </div>
             ))}
           </div>
           </div>
       )
   }
