import React from 'react';
import { useAuth } from '../Context/AuthContext';

function Home() {
  const { isAuthenticated } = useAuth();

  return (
    <div style={{ 
      minHeight: '100vh', 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      textAlign: 'center',
      padding: '20px'
    }}>
      <div style={{ maxWidth: '800px' }}>
        <h1 style={{ fontSize: '3rem', marginBottom: '20px' }}>
          Welcome to Wholesaler
        </h1>
        <p style={{ fontSize: '1.2rem', marginBottom: '30px', opacity: 0.9 }}>
          Your comprehensive inventory management solution for wholesale businesses.
          Manage products, track orders, and streamline your operations with ease.
        </p>
        
        {!isAuthenticated && (
          <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', flexWrap: 'wrap' }}>
            <button 
              onClick={() => window.location.href = '/login'}
              style={{
                padding: '12px 30px',
                fontSize: '16px',
                backgroundColor: 'rgba(255,255,255,0.2)',
                border: '2px solid white',
                color: 'white',
                borderRadius: '25px',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = 'rgba(255,255,255,0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'rgba(255,255,255,0.2)';
              }}
            >
              Get Started
            </button>
            <button 
              onClick={() => window.location.href = '/signup'}
              style={{
                padding: '12px 30px',
                fontSize: '16px',
                backgroundColor: 'white',
                border: '2px solid white',
                color: '#667eea',
                borderRadius: '25px',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#f8f9fa';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'white';
              }}
            >
              Sign Up
            </button>
          </div>
        )}
        
        <div style={{ marginTop: '50px', display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '30px' }}>
          <div style={{ padding: '20px', backgroundColor: 'rgba(255,255,255,0.1)', borderRadius: '10px' }}>
            <h3>ðŸ“¦ Inventory Management</h3>
            <p>Track your products, manage stock levels, and monitor inventory in real-time.</p>
          </div>
          <div style={{ padding: '20px', backgroundColor: 'rgba(255,255,255,0.1)', borderRadius: '10px' }}>
            <h3>ðŸ“‹ Order Tracking</h3>
            <p>Manage orders, track shipments, and keep your customers informed.</p>
          </div>
          <div style={{ padding: '20px', backgroundColor: 'rgba(255,255,255,0.1)', borderRadius: '10px' }}>
            <h3>ðŸ“Š Analytics & Reports</h3>
            <p>Get insights into your business performance with detailed reports and analytics.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
