// pages/Supplier.js
import React, { useState, useEffect } from 'react';
import SupplierCard from '../../Components/SupplierCard';
import AddSupplierModal from '../../Components/AddSupplierModal'
import '../Styles/Supplier.css';

const dummySuppliers = [
  {
    id: 1,
    name: "Global Electronics Inc.",
    type: "Wholesaler",
    industry: "Electronics",
    image: "https://via.placeholder.com/40"
  },
  {
    id: 2,
    name: "Industrial Machinery Co.",
    type: "Manufacturer",
    industry: "Machinery",
    image: "https://via.placeholder.com/40"
  },
  {
    id: 3,
    name: "NeoTech Solutions",
    type: "Distributor",
    industry: "IT Solutions",
    image: "https://via.placeholder.com/40"
  },
];

export default function Supplier() {
  const [suppliers, setSuppliers] = useState(dummySuppliers);
  const [showModal, setShowModal] = useState(false);

  return (
    <div className="supplier-container">
      <main className="main-content">
        <div className="supplier-header-row">
          <h2>Supplier Directory</h2>
          <p><b>Total Suppliers:</b> {suppliers.length}</p>
        </div>

        <div className="card-grid">
          {suppliers.map(supplier => (
            <SupplierCard key={supplier.id} supplier={supplier} />
          ))}
        </div>
      </main>
      <aside className="sidebar">
        <h2>Search & Filters</h2>
         <button className="add-button" onClick={() => setShowModal(true)}>+ Add New Supplier</button>
        {showModal && <AddSupplierModal onClose={() => setShowModal(false)} />}
        <input type="text" className="search-box" placeholder="Search suppliers..." />
        <div className="quick-links">
          <h4>Quick Links</h4>
          <a href="#">Top Suppliers</a>
          <a href="#">Recently Added</a>
          <a href="#">Expiring Contracts</a>
        </div>
      </aside>

      
    </div>
  );
}
