import React, { useEffect } from 'react';
import './Popup.css';

const Popup = ({ type, message, isVisible, onClose, duration = 3000 }) => {
  useEffect(() => {
    if (isVisible && duration > 0) {
      const timer = setTimeout(() => {
        onClose();
      }, duration);
      return () => clearTimeout(timer);
    }
  }, [isVisible, duration, onClose]);

  if (!isVisible) return null;

  const getIcon = () => {
    switch (type) {
      case 'success':
        return '✓';
      case 'error':
        return '✕';
      case 'warning':
        return '⚠';
      default:
        return 'ℹ';
    }
  };

  return (
    <div className={`popup-overlay ${isVisible ? 'show' : ''}`} onClick={onClose}>
      <div className={`popup popup-${type} ${isVisible ? 'show' : ''}`} onClick={(e) => e.stopPropagation()}>
        <div className="popup-icon">{getIcon()}</div>
        <div className="popup-message">{message}</div>
        <button className="popup-close" onClick={onClose}>×</button>
      </div>
    </div>
  );
};

export default Popup;
