import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../Styles/Inventory.css'

const columns = ["Product", "Model ID", "Size", "Stock", "Price", "Actions", "Status"];
const statusOptions = ["Pending", "Defective", "Returned"];

const TickIcon = ({ active, ...props }) => (
  <svg
    width="24" height="24" viewBox="0 0 24 24"
    fill="none"
    stroke={active ? '#2d2a81' : '#b0b8e0'}
    strokeWidth="2.5"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ verticalAlign: 'middle', transition: 'stroke 0.2s', ...props.style }}
    {...props}
  >
    <circle cx="12" cy="12" r="11" fill={active ? '#eaf0fa' : '#f3f5fa'} stroke="none" />
    <polyline points="7,13 11,17 17,9" fill="none" stroke={active ? '#2d2a81' : '#b0b8e0'} strokeWidth="2.5" />
  </svg>
);

const CrossIcon = ({ active, ...props }) => (
  <svg
    width="24" height="24" viewBox="0 0 24 24"
    fill="none"
    stroke={active ? '#dc3545' : '#b0b8e0'}
    strokeWidth="2.5"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ verticalAlign: 'middle', transition: 'stroke 0.2s', ...props.style }}
    {...props}
  >
    <circle cx="12" cy="12" r="11" fill={active ? '#fbeaec' : '#f3f5fa'} stroke="none" />
    <line x1="8" y1="8" x2="16" y2="16" />
    <line x1="16" y1="8" x2="8" y2="16" />
  </svg>
);

export default function InventoryTable (){
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [dropdownOpen, setDropdownOpen] = useState({}); // { [productId]: true/false }

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            setLoading(true);
            const response = await axios.get('http://localhost:8000/api/get-products/');
            setProducts(response.data);
            setError(null);
        } catch (err) {
            console.error('Error fetching products:', err);
            setError('Failed to load products');
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                await axios.delete(`http://localhost:8000/api/delete-product/${productId}/`);
                fetchProducts(); // Refresh the list
            } catch (err) {
                console.error('Error deleting product:', err);
                alert('Failed to delete product');
            }
        }
    };

    const updateStatus = async (productId, status) => {
        try {
            await axios.post(`http://localhost:8000/api/update-status/${productId}/`, { status });
            setDropdownOpen((prev) => ({ ...prev, [productId]: false }));
            fetchProducts();
        } catch (err) {
            alert('Failed to update status');
        }
    };

    const handleDropdown = (productId, open) => {
        setDropdownOpen((prev) => ({ ...prev, [productId]: open }));
    };

    if (loading) {
        return <div style={{ textAlign: 'center', padding: '20px' }}>Loading products...</div>;
    }

    if (error) {
        return <div style={{ textAlign: 'center', padding: '20px', color: 'red' }}>{error}</div>;
    }

    return(
        <table className="inventory-table">
        <thead>
          <tr>
            {columns.map((col, index) => (
            <th key={index}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {products.length === 0 ? (
            <tr>
              <td colSpan={7} style={{ textAlign: 'center', padding: '20px' }}>
                No products found. Add some products to get started!
              </td>
            </tr>
          ) : (
            products.map((item) => (
              <tr key={item.id}>
                <td>
                  <div className="product-info">
                    <div className="product-img-placeholder">40×40</div>
                    <div>
                      <div className="product-name">{item.name}</div>
                      <div className="product-variant">ID: {item.id}</div>
                    </div>
                  </div>
                </td>
                <td>{item.model_no}</td>
                <td>{item.size}</td>
                <td>{item.pieces}</td>
                <td>₹{parseFloat(item.price).toFixed(2)}</td>
                <td>
                  <span className="edit-link">Edit</span> 
                  <span className="delete-link" onClick={() => handleDelete(item.id)}>Delete</span>
                </td>
                <td style={{ minWidth: 140 }}>
                  <div className="status-area" style={{ display: 'flex', alignItems: 'center', gap: 8, background: '#f4f7fb', borderRadius: 10, border: '1px solid #e0e6f7', padding: '6px 10px', position: 'relative' }}>
                    <span
                      title="Mark as Received"
                      style={{ cursor: 'pointer', display: 'inline-flex', alignItems: 'center' }}
                      onClick={() => updateStatus(item.id, 'Received')}
                      onMouseOver={e => e.currentTarget.firstChild.setAttribute('stroke', '#2d2a81')}
                      onMouseOut={e => e.currentTarget.firstChild.setAttribute('stroke', item.status === 'Received' ? '#2d2a81' : '#b0b8e0')}
                    >
                      <TickIcon active={item.status === 'Received'} />
                    </span>
                    <span
                      title="Mark as Other Status"
                      style={{ cursor: 'pointer', display: 'inline-flex', alignItems: 'center' }}
                      onClick={() => handleDropdown(item.id, !dropdownOpen[item.id])}
                      onMouseOver={e => e.currentTarget.firstChild.setAttribute('stroke', '#dc3545')}
                      onMouseOut={e => e.currentTarget.firstChild.setAttribute('stroke', '#b0b8e0')}
                    >
                      <CrossIcon active={dropdownOpen[item.id] || (item.status !== 'Received' && item.status !== 'Pending')} />
                    </span>
                    {dropdownOpen[item.id] && (
                      <select
                        autoFocus
                        className="status-dropdown"
                        style={{
                          marginLeft: 8,
                          borderRadius: 6,
                          border: '1px solid #2d2a81',
                          color: '#2d2a81',
                          fontWeight: 500,
                          background: '#f4f7fb',
                          padding: '2px 6px',
                          minWidth: 80,
                          maxWidth: 110,
                          fontSize: 13,
                          height: 28,
                        }}
                        value={item.status !== 'Received' ? item.status : ''}
                        onChange={e => updateStatus(item.id, e.target.value)}
                        onBlur={() => handleDropdown(item.id, false)}
                      >
                        <option value="" disabled>Choose</option>
                        {statusOptions.map(opt => (
                          <option key={opt} value={opt}>{opt}</option>
                        ))}
                      </select>
                    )}
                    <span style={{ fontSize: 13, marginLeft: 8, color: '#2d2a81', fontWeight: 600, letterSpacing: 0.2 }}>{item.status}</span>
                  </div>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
        
    )
}
