import React, { useState, useEffect } from 'react';
import './Styles/AddProduct.css';
import axios from 'axios';

function AddProduct() {
  const [formData, setFormData] = useState({
    name: '',
    model_no: '',
    price: '',
    size: '',
    pieces: '',
    image: null,
    supplier: '',
  });

  const [products, setProducts] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8000/api/products/suppliers/')
      .then(response => setSuppliers(response.data))
      .catch(error => console.error('Error fetching suppliers:', error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value
    });
  };

  const addOrUpdateProduct = () => {
    if (!formData.name || !formData.price || !formData.pieces) {
      setMessage('❌ Please fill in at least Product Name, Price, and Quantity');
      return;
    }
    if (!formData.supplier) {
      setMessage('❌ Please select a supplier');
      return;
    }

    const updatedProduct = { ...formData };

    if (editingIndex !== null) {
      const updatedProducts = [...products];
      updatedProducts[editingIndex] = updatedProduct;
      setProducts(updatedProducts);
      setEditingIndex(null);
      setMessage('✅ Product updated');
    } else {
      setProducts([...products, updatedProduct]);
      setMessage('✅ Product added to list');
    }

    // Reset form
    setFormData({
      name: '',
      model_no: '',
      price: '',
      size: '',
      pieces: '',
      image: null,
      supplier: '',
    });
  };

  const handleEditProduct = (index) => {
    const productToEdit = products[index];
    setFormData(productToEdit);
    setEditingIndex(index);
    setMessage('✏️ Now editing product');
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleDeleteProduct = (index) => {
    const updatedProducts = [...products];
    updatedProducts.splice(index, 1);
    setProducts(updatedProducts);
    setMessage('🗑️ Product removed');
  };

  return (
    <div className="add-product-container">
      <h2>{editingIndex !== null ? 'Edit Product' : 'Add Product'}</h2>
      
      {message && <p className="message">{message}</p>}

      <div className="form-group">
        <label>Product Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
      </div>

      <div className="form-group">
        <label>Model Number:</label>
        <input type="text" name="model_no" value={formData.model_no} onChange={handleInputChange} />
      </div>

      <div className="form-group">
        <label>Price:</label>
        <input type="number" name="price" value={formData.price} onChange={handleInputChange} />
      </div>

      <div className="form-group">
        <label>Size:</label>
        <input type="text" name="size" value={formData.size} onChange={handleInputChange} />
      </div>

      <div className="form-group">
        <label>Quantity (Pieces):</label>
        <input type="number" name="pieces" value={formData.pieces} onChange={handleInputChange} />
      </div>

      <div className="form-group">
        <label>Supplier:</label>
        <select name="supplier" value={formData.supplier} onChange={handleInputChange}>
          <option value="">Select Supplier</option>
          {suppliers.map((supplier) => (
            <option key={supplier.id} value={supplier.id}>
              {supplier.name}
            </option>
          ))}
        </select>
      </div>

      <div className="form-group">
        <label>Upload Image or PDF:</label>
        <input type="file" name="image" onChange={handleInputChange} />
      </div>

      <button onClick={addOrUpdateProduct} className="submit-button">
        {editingIndex !== null ? 'Update Product' : 'Add Product'}
      </button>

      <hr />

      <h3>Product List</h3>
      {products.length === 0 ? (
        <p>No products added</p>
      ) : (
        <table className="product-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Model</th>
              <th>Price</th>
              <th>Size</th>
              <th>Quantity</th>
              <th>Supplier</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p, index) => (
              <tr key={index}>
                <td>{p.name}</td>
                <td>{p.model_no}</td>
                <td>{p.price}</td>
                <td>{p.size}</td>
                <td>{p.pieces}</td>
                <td>{p.supplier}</td>
                <td>
                  <button onClick={() => handleEditProduct(index)} className="edit-btn">Edit</button>
                  <button onClick={() => handleDeleteProduct(index)} className="delete-btn">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AddProduct;
