import React from 'react';

const DeleteConfirmationModal = ({ open, onClose, onConfirm }) => {
  if (!open) return null;
  return (
    <div className="modal-overlay" style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.2)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div className="modal-content" style={{ background: '#fff', borderRadius: 12, padding: 32, minWidth: 320, boxShadow: '0 2px 16px rgba(0,0,0,0.15)', position: 'relative' }}>
        <span className="modal-close" onClick={onClose} style={{ position: 'absolute', top: 12, right: 16, fontSize: 24, cursor: 'pointer' }}>&times;</span>
        <div style={{ textAlign: 'center', padding: 20 }}>
          <div style={{ fontSize: 48, color: '#ffcc00', marginBottom: 8 }}>!</div>
          <h2 style={{ margin: '12px 0' }}>Delete Product?</h2>
          <p style={{ marginBottom: 24 }}>Are you sure you want to delete this product?</p>
          <button onClick={onConfirm} style={{ background: '#2451a4', color: '#fff', border: 'none', borderRadius: 5, padding: '10px 28px', fontWeight: 500, fontSize: 16, cursor: 'pointer' }}>Yes, Delete</button>
        </div>
      </div>
    </div>
  );
};

export default DeleteConfirmationModal;
